PlotguideLocalData.tab={
[1] = {sortid = 1,	typeid = 1,	chapterid = 0,	chaptersort = 1,	number = "0-1",	name = "錯誤示範",	plot_first = "yd_1",	plot_last = "0",	checkpointid = 0,	picture = "ArtGallery/PlotCg/dialog_yd-1",	introduction = "失去記憶的我在未知的機器中醒來，下一秒卻出現在城市的廢墟中。一個聲音引導著我來到一個巨大的少女“屍體”前，我震驚於眼前的一切卻忽略了身後暗藏的危險......",	"0",	"0",	"0",	"0"},
[2] = {sortid = 2,	typeid = 1,	chapterid = 0,	chaptersort = 2,	number = "0-2",	name = "重逢",	plot_first = "yd_2_3",	plot_last = "0",	checkpointid = 0,	picture = "ArtGallery/PlotCg/dialog_yd-2",	introduction = "名為露露啦的巨大少女稱呼我為“領航”她的目標好像是帶我離開。正當我們準備離開之時，我腦中閃過露露啦被攻擊的畫面，但是還來不及提醒，那畫面就在我眼前重演，而隨後，世界又回到了黑暗中，直到斯卡蒂將我喚醒...剛剛的一切都是夢嗎？",	"0",	"0",	"0",	"0"},
[3] = {sortid = 3,	typeid = 1,	chapterid = 0,	chaptersort = 3,	number = "0-3",	name = "亡徒迫近",	plot_first = "yd_4_2",	plot_last = "0",	checkpointid = 0,	picture = "ArtGallery/PlotCg/dialog_yd-4",	introduction = "在我的請求下，我們開始尋找下落不明的朱利烏斯，而在前方是一隻更巨大，更可怕的怪物——准罪級原罪【索貝克】......",	"0",	"0",	"0",	"0"},
[4] = {sortid = 4,	typeid = 1,	chapterid = 0,	chaptersort = 4,	number = "0-4",	name = "『\u{3000}\u{3000}』過往",	plot_first = "yd_5_2",	plot_last = "0",	checkpointid = 0,	picture = "ArtGallery/PlotCg/dialog_yd-5",	introduction = "在芙蘿拉的支援下，我們擊潰了【索貝克】，可當我找到朱利烏斯時，【她】也找到了我......",	"0",	"0",	"0",	"0"},
[5] = {sortid = 5,	typeid = 1,	chapterid = 0,	chaptersort = 5,	number = "0-5",	name = "新生",	plot_first = "yd_6_2",	plot_last = "0",	checkpointid = 0,	picture = "ArtGallery/PlotCg/dialog_yd-6",	introduction = "一切都結束了，一切又重新開始了。",	"0",	"0",	"0",	"0"},
[6] = {sortid = 6,	typeid = 1,	chapterid = 1,	chaptersort = 1,	number = "1-1",	name = "失憶症",	plot_first = "s_1",	plot_last = "0",	checkpointid = 100000,	picture = "ArtGallery/PlotCg/dialog_s-1",	introduction = "從那天起，我便失去了過去，但取而代之的是被稱之為通感的能力。對領航員的身份而言，這毫無疑問是一項戰略級的能力，但對於身為“人”的我而言，這更像是一個詛咒……",	"0",	"0",	"0",	"0"},
[7] = {sortid = 7,	typeid = 1,	chapterid = 1,	chaptersort = 2,	number = "1-2",	name = "戰前會議",	plot_first = "s_2",	plot_last = "0",	checkpointid = 100001,	picture = "ArtGallery/PlotCg/dialog_s-2",	introduction = "我與我的情報官吉雅，一起來參加了【艾吉斯】的動員會議，雖說是情報官，但實際上吉雅是我的“妹妹”，她患有著四肢無力的病症，今天是她第一天以情報官的身份和我站在一起……",	"0",	"0",	"0",	"0"},
[8] = {sortid = 8,	typeid = 1,	chapterid = 1,	chaptersort = 3,	number = "1-9",	name = "【蒼穹戰線】",	plot_first = "s_3",	plot_last = "0",	checkpointid = 100008,	picture = "ArtGallery/PlotCg/dialog_s-3",	introduction = "會議開始，代理人宣佈人類對原罪的戰爭已經進行到了最後階段，DOMINATOR（殲星炮）的啟動能源與技術已經完全落實，人類的大反擊作戰【蒼穹戰線】計劃將被啟動……",	"0",	"0",	"0",	"0"},
[9] = {sortid = 9,	typeid = 1,	chapterid = 1,	chaptersort = 4,	number = "1-10",	name = "支配者",	plot_first = "s_4",	plot_last = "0",	checkpointid = 100009,	picture = "ArtGallery/PlotCg/dialog_s-4",	introduction = "【蒼穹戰線】的真面目是用新研製的戰略兵器DOMINATOR直擊原罪的始祖【逆愛】，為此需要把搭載DOMINATOR的卡佳支部方舟發射到近月軌道上，這正是本次作戰卡佳支部的任務……",	"0",	"0",	"0",	"0"},
[10] = {sortid = 10,	typeid = 1,	chapterid = 1,	chaptersort = 5,	number = "1-17",	name = "“母親”",	plot_first = "s_5",	plot_last = "0",	checkpointid = 100016,	picture = "ArtGallery/PlotCg/dialog_s-5",	introduction = "會議後我獨自留了下來，因為收養我的“母親”，同時也是艾吉斯首席科學官的史蒂芬妮博士想要見我，她給我帶來了新的任務——我將成為【蒼穹戰線】計劃的關鍵……",	"0",	"0",	"0",	"0"},
[11] = {sortid = 11,	typeid = 1,	chapterid = 1,	chaptersort = 6,	number = "1-18",	name = "“預知夢”",	plot_first = "s_6",	plot_last = "0",	checkpointid = 100017,	picture = "ArtGallery/PlotCg/dialog_s-6",	introduction = "在與史蒂芬妮博士爭辯的過程中，我的通感能力不受控制地啟動了，我看見了…一片火海……這是始祖原罪【逆愛】為地球預演的結局，無論多少次，我都沒法抑制住這股發自內心的恐懼以及憤怒……",	"0",	"0",	"0",	"0"},
[12] = {sortid = 12,	typeid = 1,	chapterid = 2,	chaptersort = 1,	number = "2-1",	name = "月之背脊",	plot_first = "s_7",	plot_last = "0",	checkpointid = 100021,	picture = "ArtGallery/PlotCg/dialog_s-7",	introduction = "一週後，我與多瑙特別聯合部隊的駕駛員們來到了月之背脊，但是剛一登陸便遭到了原罪的包圍，史蒂芬妮博士也失去聯絡，甚至DOMINATOR都沒有反應，我試圖修復DOMINATOR的控制器……",	"0",	"0",	"0",	"0"},
[13] = {sortid = 13,	typeid = 1,	chapterid = 2,	chaptersort = 2,	number = "2-5",	name = "奪秒",	plot_first = "s_8",	plot_last = "0",	checkpointid = 100025,	picture = "ArtGallery/PlotCg/dialog_s-8",	introduction = "控制器修復失敗，隊員們已經快堅持不住，只剩下最後的辦法了，雖然史蒂芬妮博士提醒過我不要這麼做，但此刻也許我該賭一賭——通過通感銜接DOMINATOR，控制它發射逆轉錄光線……",	"0",	"0",	"0",	"0"},
[14] = {sortid = 14,	typeid = 1,	chapterid = 2,	chaptersort = 3,	number = "2-9",	name = "謎之聲",	plot_first = "s_9",	plot_last = "0",	checkpointid = 100029,	picture = "ArtGallery/PlotCg/dialog_s-9",	introduction = "神秘的身影引導著我，她自稱是【逆愛】的【子嗣】，想要違抗母親的命令，願意借給我力量，幫助我度過眼前的原罪潮，我不得不接受，並使用了這份力量，但這也間接導致原罪的首領【逆愛】發現了我……",	"0",	"0",	"0",	"0"},
[15] = {sortid = 15,	typeid = 1,	chapterid = 2,	chaptersort = 4,	number = "2-11",	name = "逆愛",	plot_first = "s_10",	plot_last = "0",	checkpointid = 100031,	picture = "ArtGallery/PlotCg/dialog_s-10",	introduction = "我們直面了她，如同山脈一般的巨影橫在了我們的面前……使得巨大化的駕駛員們看起來也宛若常人，這便是人類最後的敵人，始祖原罪【逆愛】，我試圖用通感的能力干涉她，卻失敗了……",	"0",	"0",	"0",	"0"},
[16] = {sortid = 16,	typeid = 1,	chapterid = 2,	chaptersort = 5,	number = "2-12",	name = "偏移的軌跡",	plot_first = "s_11",	plot_last = "0",	checkpointid = 100032,	picture = "ArtGallery/PlotCg/dialog_s-11",	introduction = "4月30日下午2點58分48秒，在謎之聲的幫助下，DOMINATOR成功發射……人類終於迎來了漫長的戰爭的勝利，但是為什麼！！……為什麼逆轉錄光線射偏了？！第一發光束，僅僅只摧毀了逆愛身邊的一扇【門】",	"0",	"0",	"0",	"0"},
[17] = {sortid = 17,	typeid = 1,	chapterid = 2,	chaptersort = 6,	number = "2-13",	name = "謝幕",	plot_first = "s_12",	plot_last = "0",	checkpointid = 100033,	picture = "ArtGallery/PlotCg/dialog_s-12",	introduction = "原罪的始祖【逆愛】死亡確認，【逆愛】的【子嗣】通過自己的能力擊殺了她，這是歷史上第一次出現原罪擊殺原罪的事例，難道說是……因為【門】被摧毀的緣故？！【子嗣】開始發射第二DOMINATOR……",	"0",	"0",	"0",	"0"},
[18] = {sortid = 18,	typeid = 1,	chapterid = 2,	chaptersort = 7,	number = "2-15",	name = "審判日",	plot_first = "s_13",	plot_last = "0",	checkpointid = 100035,	picture = "ArtGallery/PlotCg/dialog_s-13",	introduction = "在DOMINATOR的炮擊下，多瑙首都區近乎3分之1的區域在瞬間被蒸發掉了化為廢墟，一對雙子原罪靜靜地注視著這一切，她們似乎對人類的結局司空見慣，為了改變世界線，決定賦予領航新的能力……",	"0",	"0",	"0",	"0"},
[19] = {sortid = 19,	typeid = 1,	chapterid = 2,	chaptersort = 8,	number = "2-16",	name = "世界線的終結",	plot_first = "s_14",	plot_last = "0",	checkpointid = 100036,	picture = "ArtGallery/PlotCg/dialog_s-14",	introduction = "【蒼穹戰線】成功了，人類終於討滅了始祖原罪【逆愛】，但也失敗了，人類親手造就了新的敵人，一位妄圖創造新世界的敵人，她掌握著人類的最終兵器，要立起新世界的準則……",	"0",	"0",	"0",	"0"},
[20] = {sortid = 20,	typeid = 1,	chapterid = 2,	chaptersort = 9,	number = "2-19",	name = "重啟Remake",	plot_first = "s_15",	plot_last = "0",	checkpointid = 100039,	picture = "ArtGallery/PlotCg/dialog_s-15",	introduction = "一切都已經結束了，我已經什麼都做不到了……在彌留之際，我仿佛幻視一般，看見了雙子的原罪，她們在詢問我，是否想要再來一次的機會……是否想要……挽回這一切……",	"0",	"0",	"0",	"0"},
[21] = {sortid = 21,	typeid = 1,	chapterid = 3,	chaptersort = 1,	number = "3-1",	name = "“噩夢”",	plot_first = "1_1",	plot_last = "0",	checkpointid = 100100,	picture = "ArtGallery/PlotCg/dialog_1-1",	introduction = "從一場噩夢中掙脫出來，我被驚醒了，但任憑我如何回憶，卻記不起半點內容，隨後響起的警告聲提示著我，多瑙支部負責的區域遭受了原罪的入侵，作為一名【領航】，我需要行動起來……",	"0",	"0",	"0",	"0"},
[22] = {sortid = 22,	typeid = 1,	chapterid = 3,	chaptersort = 2,	number = "3-2",	name = "低等原罪",	plot_first = "1_2",	plot_last = "0",	checkpointid = 100101,	picture = "ArtGallery/PlotCg/dialog_1-2",	introduction = "當原罪的恐懼籠罩在人們頭上時，一部分人選擇逃跑時，另一部分人便會選擇迎擊。我從戰場上拯救了一位士兵，他便是選擇迎擊的其中之一，隨後，在原罪的攻擊下，我的小隊的第一位成員也登場了……",	"0",	"0",	"0",	"0"},
[23] = {sortid = 23,	typeid = 1,	chapterid = 3,	chaptersort = 3,	number = "3-4",	name = "特別聯合",	plot_first = "1_3",	plot_last = "0",	checkpointid = 100103,	picture = "ArtGallery/PlotCg/dialog_1-3",	introduction = "在朱利烏斯颯爽登場之後，陸續著又來了兩名巨大化的少女。一等兵查麗莎·德·洛蘭與支援型的弗蘭卡，就她們的戰鬥特性我開始擬定剿滅原罪、拯救平民的辦法，希望她們接下的相處能融洽一點。",	"0",	"0",	"0",	"0"},
[24] = {sortid = 24,	typeid = 1,	chapterid = 3,	chaptersort = 4,	number = "3-5",	name = "問題小隊",	plot_first = "1_4",	plot_last = "0",	checkpointid = 100104,	picture = "ArtGallery/PlotCg/dialog_1-4",	introduction = "朱利烏斯負責在弗蘭卡能支援到的前沿處理原罪，弗蘭卡活動在隊伍的後方支援，而機動性最高的查麗莎則是處理邊緣或者前線漏掉的原罪，如果能這樣有序地推進就好了，但是查麗莎似乎不滿意……",	"0",	"0",	"0",	"0"},
[25] = {sortid = 25,	typeid = 1,	chapterid = 3,	chaptersort = 5,	number = "3-6",	name = "逆行",	plot_first = "1_5",	plot_last = "0",	checkpointid = 100105,	picture = "ArtGallery/PlotCg/dialog_1-5",	introduction = "這座城市，化為了煉獄，巨大化的少女和原罪們在廝殺之間無意地波及了周圍，大地在不斷震動著，鋼筋水泥鑄成的大樓在搖晃坍塌，有一位母親與她的女兒走散了……",	"0",	"0",	"0",	"0"},
[26] = {sortid = 26,	typeid = 1,	chapterid = 3,	chaptersort = 6,	number = "3-7",	name = "匯聚之處",	plot_first = "1_6",	plot_last = "0",	checkpointid = 100106,	picture = "ArtGallery/PlotCg/dialog_1-6",	introduction = "弗蘭卡及時趕到拯救了這對母女，但是人們對巨大少女這一技術有所瞭解，也明確地知道她們是人類這件事，可真正面對這些少女的時候，這位母親還是遲疑了，最終弗蘭卡真切的眼神讓母女接受了她的幫助",	"0",	"0",	"0",	"0"},
[27] = {sortid = 27,	typeid = 1,	chapterid = 3,	chaptersort = 7,	number = "3-8",	name = "通感者",	plot_first = "1_7",	plot_last = "0",	checkpointid = 100107,	picture = "ArtGallery/PlotCg/dialog_1-7",	introduction = "同一時間，從對抗原罪的戰爭最前線調任過來的朱利烏斯，也因為在城市中戰鬥而束手束腳，過於在意自己的行為會不會讓正在撤離的人們受傷，為了安撫她的情緒，領航展開了名為【通感】的能力。",	"0",	"0",	"0",	"0"},
[28] = {sortid = 28,	typeid = 1,	chapterid = 3,	chaptersort = 8,	number = "3-9",	name = "合理安排",	plot_first = "1_8",	plot_last = "0",	checkpointid = 100108,	picture = "ArtGallery/PlotCg/dialog_1-8",	introduction = "朱利烏斯穩定下來之後，查麗莎的不滿又通過聯絡裝置傳遞過來，她似乎對於自己不能在正面作戰十分不滿，僅僅在側翼遊走並不能發揮她的全部戰力……真是的，一意孤行在任何時候都不是好性格，真希望她改掉。",	"0",	"0",	"0",	"0"},
[29] = {sortid = 29,	typeid = 1,	chapterid = 3,	chaptersort = 9,	number = "3-12",	name = "女武神",	plot_first = "1_9",	plot_last = "0",	checkpointid = 100111,	picture = "ArtGallery/PlotCg/dialog_1-9",	introduction = "不安，從作戰開始就一直纏繞著我的不安，源自被濃霧完全遮蔽的海面，於此同時，查麗莎終於不再聽從我的命令，自顧自地衝到了朱利烏斯身邊，也即是正面……",	"0",	"0",	"0",	"0"},
[30] = {sortid = 30,	typeid = 1,	chapterid = 3,	chaptersort = 10,	number = "3-13",	name = "分歧點",	plot_first = "1_10",	plot_last = "0",	checkpointid = 100112,	picture = "ArtGallery/PlotCg/dialog_1-10",	introduction = "火力全開的查麗莎，確實讓我見識到了什麼是真正的【超高速戰鬥】，但是，她這份神勇的表現並不能讓我安心，倒不如說，讓我心中的不安加劇了。一隻蜘蛛型原罪在我們爭論的時候出現了……",	"0",	"0",	"0",	"0"},
[31] = {sortid = 31,	typeid = 1,	chapterid = 3,	chaptersort = 11,	number = "3-18",	name = "失航",	plot_first = "1_11",	plot_last = "1_11e",	checkpointid = 100117,	picture = "ArtGallery/PlotCg/dialog_1-11",	introduction = "從爆炸的蜘蛛身體內湧出的有毒氣體向四周擴散，我操縱著機動裝置急速拉高了位置，弗蘭卡帶著民眾向遠離毒氣的方向轉移，但此時的戰場的陣型已經被破壞，作為支援型的弗蘭卡率先受到了襲擊，失去了聯絡……",	"0",	"0",	"0",	"0"},
[32] = {sortid = 32,	typeid = 1,	chapterid = 3,	chaptersort = 12,	number = "3-19",	name = "心火燃起",	plot_first = "1_12",	plot_last = "0",	checkpointid = 100118,	picture = "ArtGallery/PlotCg/dialog_1-12",	introduction = "弗蘭卡為了守護人們的安危而死死支撐著，此時終於意識到自己失誤的查麗莎趕到了現場，見識到了弗蘭卡慘狀，憤怒之下的她追著襲擊者——螳螂型原罪深入了毒霧中……",	"0",	"0",	"0",	"0"},
[33] = {sortid = 33,	typeid = 1,	chapterid = 4,	chaptersort = 1,	number = "4-4",	name = "兩方對峙",	plot_first = "1_13",	plot_last = "0",	checkpointid = 100122,	picture = "ArtGallery/PlotCg/dialog_1-13",	introduction = "濃霧中，查麗莎與原罪正在對峙著，這是一場獵殺者和獵物的對決，但究竟誰是獵物還猶未可知，雙方不斷地試探，查麗莎抱持著以傷換傷的意志，似乎打算與原罪同歸於盡……",	"0",	"0",	"0",	"0"},
[34] = {sortid = 34,	typeid = 1,	chapterid = 4,	chaptersort = 2,	number = "4-9",	name = "協同",	plot_first = "1_14",	plot_last = "1_14e",	checkpointid = 100127,	picture = "ArtGallery/PlotCg/dialog_1-14",	introduction = "雙方互相瞄準對方的頭部，給予對方致命一擊——但這場絕對卻被突如其來的身影中斷了，朱利烏斯及時趕到了現場，螳螂型原罪再度撤走，領航與查麗莎建立了【通感】分擔了她的痛苦……",	"0",	"0",	"0",	"0"},
[35] = {sortid = 35,	typeid = 1,	chapterid = 4,	chaptersort = 3,	number = "4-10",	name = "准罪級",	plot_first = "1_15",	plot_last = "0",	checkpointid = 100128,	picture = "ArtGallery/PlotCg/dialog_1-15",	introduction = "我一直以來的擔憂成為了現實，准罪級原罪——【索貝克】從海中登陸了，地面部隊的常規火力無法對它造成一絲一毫的傷害，反而激怒了它，而在它發洩一通吼，把目光放在正在撤離的我們身上。",	"0",	"0",	"0",	"0"},
[36] = {sortid = 36,	typeid = 1,	chapterid = 4,	chaptersort = 4,	number = "4-11",	name = "民眾之盾",	plot_first = "1_16",	plot_last = "0",	checkpointid = 100129,	picture = "ArtGallery/PlotCg/dialog_1-16",	introduction = "預計在五分鐘後，【索貝爾】會與我們小隊進行第一次接觸，我下了一個決斷，讓查麗莎迅速清理周邊的原罪的，然後讓平民們先撤離，我們要在這裡攔下【索貝爾】！",	"0",	"0",	"0",	"0"},
[37] = {sortid = 37,	typeid = 1,	chapterid = 4,	chaptersort = 5,	number = "4-15",	name = "阻截戰",	plot_first = "1_18",	plot_last = "0",	checkpointid = 100133,	picture = "ArtGallery/PlotCg/dialog_1-18",	introduction = "強敵帶給我們的壓迫感、生死感，讓人窒息，但同時，經過重重篩選進入這支特別小隊性格迥異的三人卻因此開始磨合，小隊的雛形在這場遭遇戰中顯現，我們將會跨過這道難關，把准罪級原罪【索貝爾】化為“食糧”。",	"0",	"0",	"0",	"0"},
[38] = {sortid = 38,	typeid = 1,	chapterid = 4,	chaptersort = 6,	number = "4-16",	name = "化身為怪物",	plot_first = "1_19",	plot_last = "0",	checkpointid = 100134,	picture = "ArtGallery/PlotCg/dialog_1-19",	introduction = "我準備把戰線拉扯到海邊，這樣需要顧及的東西就少了很多，駕駛員們開始不斷地配合著，為了打倒怪物——自身不惜化為怪物，這就是我們人類啊……",	"0",	"0",	"0",	"0"},
[39] = {sortid = 39,	typeid = 1,	chapterid = 4,	chaptersort = 7,	number = "4-17",	name = "生還者",	plot_first = "1_20",	plot_last = "0",	checkpointid = 100135,	picture = "ArtGallery/PlotCg/dialog_1-20",	introduction = "多瑙特別聯合部隊的首戰，以准罪級原罪【索貝爾】的死亡告捷，但是濃霧卻尚未散開，從霧中又有一道巨大的身影緩緩走出，是一位傷痕累累的少女……",	"0",	"0",	"0",	"0"},
[40] = {sortid = 40,	typeid = 1,	chapterid = 5,	chaptersort = 1,	number = "5-1",	name = "與會人員",	plot_first = "2_1",	plot_last = "0",	checkpointid = 100200,	picture = "ArtGallery/PlotCg/dialog_2-1",	introduction = "多洛莉絲，她是第三研究室防衛隊的駕駛員，也是在霧中被我們救出的少女，我們從她口中得知了濃霧仍未散去的真相，又有一隻【高等原罪】誕生了。",	"0",	"0",	"0",	"0"},
[41] = {sortid = 41,	typeid = 1,	chapterid = 5,	chaptersort = 2,	number = "5-2",	name = "記憶失卻",	plot_first = "2_2",	plot_last = "0",	checkpointid = 100201,	picture = "ArtGallery/PlotCg/dialog_2-2",	introduction = "出擊前的準備，多洛莉絲似乎因為蒼霧的影響什麼都想不起來了，她似乎認為自己是第三研究室防衛隊的逃兵，大家並不那麼認為，並且開始開導多洛莉絲……",	"0",	"0",	"0",	"0"},
[42] = {sortid = 42,	typeid = 1,	chapterid = 5,	chaptersort = 3,	number = "5-6",	name = "月冕小隊",	plot_first = "2_3",	plot_last = "0",	checkpointid = 100205,	picture = "ArtGallery/PlotCg/dialog_2-3",	introduction = "為了阻止新生的原罪拿到位於第三研究室內的【貪婪核心】，我們在海面上加速行進著，途中遇到了同屬於多瑙的隊伍——月冕小隊……她們隊伍好像也都是怪人……",	"0",	"0",	"0",	"0"},
[43] = {sortid = 43,	typeid = 1,	chapterid = 5,	chaptersort = 4,	number = "5-8",	name = "“魚”",	plot_first = "2_4",	plot_last = "0",	checkpointid = 100207,	picture = "ArtGallery/PlotCg/dialog_2-4",	introduction = "從月冕小隊的對戰摩根勒菲口中得知，有一只巨大的原罪在海中出沒，這讓我感到不妙，我們最壞的情況可能已經發生了——新生的高等原罪已經吞噬了【貪婪核心】，發生了異變。",	"0",	"0",	"0",	"0"},
[44] = {sortid = 44,	typeid = 1,	chapterid = 5,	chaptersort = 5,	number = "5-9",	name = "弱小的她",	plot_first = "2_5",	plot_last = "0",	checkpointid = 100208,	picture = "ArtGallery/PlotCg/dialog_2-5",	introduction = "這是第三研究院防衛隊的故事，是多洛莉絲暫時遺落掉的記憶，沒關係，就算暫時遺忘，記憶也會馬上追上你的，這是多洛莉絲，最幸福的時光。",	"0",	"0",	"0",	"0"},
[45] = {sortid = 45,	typeid = 1,	chapterid = 5,	chaptersort = 6,	number = "5-11",	name = "適格率",	plot_first = "2_6",	plot_last = "0",	checkpointid = 100210,	picture = "ArtGallery/PlotCg/dialog_2-6",	introduction = "我們進入了蒼霧區，探測儀器如情報說得一樣失去了作用，不是100%適格率的多洛莉絲受到蒼霧的影響最大，雖然我的【通感】能緩和這種跡象，但是我並不能一直保持著鏈接，因為接下裡需要靠我的能力，來找到通往第三實驗室的路……",	"0",	"0",	"0",	"0"},
[46] = {sortid = 46,	typeid = 1,	chapterid = 5,	chaptersort = 7,	number = "5-13",	name = "回憶補時",	plot_first = "2_7",	plot_last = "0",	checkpointid = 100212,	picture = "ArtGallery/PlotCg/dialog_2-7",	introduction = "消滅了沿路的原罪後，我觀測到了兩個強大的尼姆比斯能源反應，推測一隻是【高等原罪】，另一隻是月冕小隊觀測到的巨大原罪，此時多洛莉絲的記憶回流，察覺到她狀態不對，我們決定返航。",	"0",	"0",	"0",	"0"},
[47] = {sortid = 47,	typeid = 1,	chapterid = 5,	chaptersort = 8,	number = "5-15",	name = "安潔莉卡",	plot_first = "2_8",	plot_last = "2_8e",	checkpointid = 100214,	picture = "ArtGallery/PlotCg/dialog_2-8",	introduction = "原罪潛伏在我們返程路上，像是有預謀地針對著我們發起襲擊，我為了緩解多洛莉絲的痛苦，與她建立了【通感】，悲傷到近乎哀慟的情緒如河流般奔入我的腦中……隨後我們遭遇了巨大的原罪——",	"0",	"0",	"0",	"0"},
[48] = {sortid = 48,	typeid = 1,	chapterid = 5,	chaptersort = 9,	number = "5-17",	name = "幕間Ⅰ",	plot_first = "2_10",	plot_last = "0",	checkpointid = 100216,	picture = "ArtGallery/PlotCg/dialog_2-10",	introduction = "恐懼、絕望、哀痛，以及——勇氣，這是我對這段記憶主觀到可以說是一己之見的印象，第三研究院防衛隊隊長，安潔莉卡的故事拉開了序幕，而又快速落幕……真的落幕了嗎……？",	"0",	"0",	"0",	"0"},
[49] = {sortid = 49,	typeid = 1,	chapterid = 5,	chaptersort = 10,	number = "5-18",	name = "逃走的意義",	plot_first = "2_11",	plot_last = "0",	checkpointid = 100217,	picture = "ArtGallery/PlotCg/dialog_2-11",	introduction = "我們在蒼霧的週邊遇見了等待著我們的月冕小隊，此時的多洛莉絲也終於從痛苦中緩和了過了，並找回了屬於她的記憶，屬於她的使命——她是一一枚鑰匙，只有她能開通通往第三實驗室的大門。",	"0",	"0",	"0",	"0"},
[50] = {sortid = 50,	typeid = 1,	chapterid = 5,	chaptersort = 11,	number = "5-19",	name = "救世主",	plot_first = "2_12",	plot_last = "0",	checkpointid = 100218,	picture = "ArtGallery/PlotCg/dialog_2-12",	introduction = "第三研究院防衛隊的隊長，為了保護核心區的核心，啟動了防禦系統，使得我們有二十四小時的準備時間來攻破第三研究院，但眼下，被命名為【耶夢加得】的巨蛇是我們目前最大的阻礙。",	"0",	"0",	"0",	"0"},
[51] = {sortid = 51,	typeid = 1,	chapterid = 5,	chaptersort = 12,	number = "5-20",	name = "幕間Ⅱ",	plot_first = "2_13",	plot_last = "0",	checkpointid = 100219,	picture = "ArtGallery/PlotCg/dialog_2-13",	introduction = "第三研究院防衛隊的成員們在蒼霧的影響下漸漸失去了自己的記憶，但是，不知道有什麼力量、什麼東西、什麼想法，讓她們一直在苦苦支撐著、支撐著……",	"0",	"0",	"0",	"0"},
[52] = {sortid = 52,	typeid = 1,	chapterid = 6,	chaptersort = 1,	number = "6-3",	name = "“幻聽”",	plot_first = "2_14",	plot_last = "0",	checkpointid = 100222,	picture = "ArtGallery/PlotCg/dialog_2-14",	introduction = "與博士確認了下一步的作戰方針後，我們返回了方舟，但吉雅的狀況有些不太對，吉雅好像感冒了……但是不中用的我似乎還被吉雅開導了……在我離去不久之後，吉雅聽到了一個聲音在呼喚她……",	"0",	"0",	"0",	"0"},
[53] = {sortid = 53,	typeid = 1,	chapterid = 6,	chaptersort = 2,	number = "6-7",	name = "夜語",	plot_first = "2_15",	plot_last = "0",	checkpointid = 100226,	picture = "ArtGallery/PlotCg/dialog_2-15",	introduction = "我獨自一人來到了方舟的甲板上，遇見了同樣心事重重的多洛莉絲，我對多洛莉絲表達了歉意，因為我們的首要任務目標……並不是救出她的隊友，而是擊破高等原罪——【嫉妒】，但我會盡自己最大的努力……",	"0",	"0",	"0",	"0"},
[54] = {sortid = 54,	typeid = 1,	chapterid = 6,	chaptersort = 3,	number = "6-11",	name = "是戰前動員呢",	plot_first = "2_16",	plot_last = "0",	checkpointid = 100230,	picture = "ArtGallery/PlotCg/dialog_2-16",	introduction = "這次的行動將會有許多小隊加入戰鬥，作為清剿低級原罪的主力，而我們小隊則是沿著開闢的路線深入蒼霧中，目標是【耶夢加得】，弗蘭卡似乎第一次見到這麼多駕駛員……",	"0",	"0",	"0",	"0"},
[55] = {sortid = 55,	typeid = 1,	chapterid = 6,	chaptersort = 4,	number = "6-14",	name = "蒼霧擴張",	plot_first = "2_17",	plot_last = "0",	checkpointid = 100233,	picture = "ArtGallery/PlotCg/dialog_2-17",	introduction = "蒼霧還在繼續擴張所侵佔的領土，一旦霧氣擴散至整個城市，情勢就會變得險峻，蒼霧的擴張，代表著耶夢加得正在成長，我們需要加快擊破【耶夢加得】的速度了……",	"0",	"0",	"0",	"0"},
[56] = {sortid = 56,	typeid = 1,	chapterid = 6,	chaptersort = 5,	number = "6-16",	name = "重返",	plot_first = "2_18",	plot_last = "0",	checkpointid = 100235,	picture = "ArtGallery/PlotCg/dialog_2-18",	introduction = "在進入蒼霧之前，所有的領航都被下達了停留在蒼霧外的指令，只有我們小隊無視了這一指令，進入了蒼霧，即使大家在蒼霧中迷失，也有我可以引導方向，【耶夢加得】討伐戰，正式開始了。",	"0",	"0",	"0",	"0"},
[57] = {sortid = 57,	typeid = 1,	chapterid = 6,	chaptersort = 6,	number = "6-17",	name = "臨高垂釣",	plot_first = "2_19",	plot_last = "0",	checkpointid = 100236,	picture = "ArtGallery/PlotCg/dialog_2-19",	introduction = "附近的原罪已經全部消滅，但是，沒有發現耶夢加得的蹤跡，我打算用自己做誘餌，用【通感】去觸動耶夢加得，我是一位領航，這就是一位領航該做的事……隨後【耶夢加得】果然上鉤了……",	"0",	"0",	"0",	"0"},
[58] = {sortid = 58,	typeid = 1,	chapterid = 6,	chaptersort = 7,	number = "6-18",	name = "塵世巨蟒",	plot_first = "2_20",	plot_last = "0",	checkpointid = 100237,	picture = "ArtGallery/PlotCg/dialog_2-20",	introduction = "與【耶夢加得】的戰鬥結束了……我們搶先殺死了正在匯聚能量的【耶夢加得】，在靠近第三研究院的時候，多洛莉絲的通訊器裡接收到了一個通訊……這個時候的她，還不知道這意味著什麼……",	"0",	"0",	"0",	"0"},
[59] = {sortid = 59,	typeid = 1,	chapterid = 7,	chaptersort = 1,	number = "7-1",	name = "近臨彼岸",	plot_first = "3_1",	plot_last = "0",	checkpointid = 100300,	picture = "ArtGallery/PlotCg/dialog_3-1",	introduction = "多瑙特別聯合部隊護送著多洛莉絲繼續前往第三研究室，目標是貪婪核心，而同一時間原罪【嫉妒】開始與【逆愛】接觸……",	"0",	"0",	"0",	"0"},
[60] = {sortid = 60,	typeid = 1,	chapterid = 7,	chaptersort = 2,	number = "7-3",	name = "霧海失者",	plot_first = "3_2",	plot_last = "0",	checkpointid = 100302,	picture = "ArtGallery/PlotCg/dialog_3-2",	introduction = "【月冕】小隊歸航，在途中遇到了落單的駕駛員，她的隊友已不知所蹤，不知生死……",	"0",	"0",	"0",	"0"},
[61] = {sortid = 61,	typeid = 1,	chapterid = 7,	chaptersort = 3,	number = "7-4",	name = "時此失航",	plot_first = "3_3",	plot_last = "0",	checkpointid = 100303,	picture = "ArtGallery/PlotCg/dialog_3-3",	introduction = "即使是100%適格率的駕駛員，長時間處於蒼霧中也難免會受到影響，輕微喪失了方向感、時間感，所幸她們距離目的地已經很近了……",	"0",	"0",	"0",	"0"},
[62] = {sortid = 62,	typeid = 1,	chapterid = 7,	chaptersort = 4,	number = "7-5",	name = "領路人",	plot_first = "3_4",	plot_last = "0",	checkpointid = 100304,	picture = "ArtGallery/PlotCg/dialog_3-4",	introduction = "低等原罪們在霧中出現，攔住了我們的去路，多洛莉絲提議從水中走，但是查麗莎……不會游泳！",	"0",	"0",	"0",	"0"},
[63] = {sortid = 63,	typeid = 1,	chapterid = 7,	chaptersort = 5,	number = "7-6",	name = "登臨",	plot_first = "3_5",	plot_last = "0",	checkpointid = 100305,	picture = "ArtGallery/PlotCg/dialog_3-5",	introduction = "弗蘭卡帶上我，朱利烏斯帶著查麗莎，在多洛莉絲的帶領下，從水中潛遊直達第三研究室，但是成群的原罪緊隨其後……",	"0",	"0",	"0",	"0"},
[64] = {sortid = 64,	typeid = 1,	chapterid = 7,	chaptersort = 6,	number = "7-7",	name = "防禦系統",	plot_first = "3_6",	plot_last = "0",	checkpointid = 100306,	picture = "ArtGallery/PlotCg/dialog_3-6",	introduction = "多洛莉絲動用了第三研究室的防禦系統“德莫斯”清除了追趕來的低等原罪，第三研究室的大門終於敞開了……但是……",	"0",	"0",	"0",	"0"},
[65] = {sortid = 65,	typeid = 1,	chapterid = 7,	chaptersort = 7,	number = "7-10",	name = "第三研究室",	plot_first = "3_7",	plot_last = "0",	checkpointid = 100309,	picture = "ArtGallery/PlotCg/dialog_3-7",	introduction = "我們終於踏足了第三研究室，一進入就受到了原罪的攻擊……",	"0",	"0",	"0",	"0"},
[66] = {sortid = 66,	typeid = 1,	chapterid = 7,	chaptersort = 8,	number = "7-11",	name = "留餘微光",	plot_first = "3_8",	plot_last = "0",	checkpointid = 100310,	picture = "ArtGallery/PlotCg/dialog_3-8",	introduction = "在消滅完敵人之後，我們接到了來自第三研究室防衛班的通信……是多洛莉絲的隊長，防衛班的倖存者們的通話……",	"0",	"0",	"0",	"0"},
[67] = {sortid = 67,	typeid = 1,	chapterid = 7,	chaptersort = 9,	number = "7-12",	name = "深坑",	plot_first = "3_9",	plot_last = "0",	checkpointid = 100311,	picture = "ArtGallery/PlotCg/dialog_3-9",	introduction = "通往研究室腹地的升降井裡佈滿了原罪，但留給我們的時間已經不多了。",	"0",	"0",	"0",	"0"},
[68] = {sortid = 68,	typeid = 1,	chapterid = 7,	chaptersort = 10,	number = "7-13",	name = "墜落",	plot_first = "3_10",	plot_last = "3_10e",	checkpointid = 100312,	picture = "ArtGallery/PlotCg/dialog_3-10",	introduction = "大家決定強行突破升降井。",	"0",	"0",	"0",	"0"},
[69] = {sortid = 69,	typeid = 1,	chapterid = 7,	chaptersort = 11,	number = "7-14",	name = "暫時離隊",	plot_first = "3_11",	plot_last = "0",	checkpointid = 100313,	picture = "ArtGallery/PlotCg/dialog_3-11",	introduction = "朱利烏斯重創，她希望隊伍能放棄她繼續前進……但風中夾雜著尼姆比斯液的味道，讓她重新燃起了回歸的希望。",	"0",	"0",	"0",	"0"},
