[79] = {sortid = 79,	typeid = 2,	chapterid = 4,	chaptersort = 1,	number = "1-1",	name = "流浪貓與偽通感者們",	plot_first = "YM/YM_1_1",	plot_last = "0",	checkpointid = 900201,	picture = "ArtGallery/PlotCg/YM/ym_1",	introduction = "【嫉妒】事件之後，艾吉斯召集了駕駛員處理薩爾市近海的防衛與清理工作，在會議之後主持會議的洛尼絲要求月冕的隊長摩根勒菲單獨留下來，並遞給了她一張女孩的照片，洛尼絲希望她能救救照片中的女孩——芙露露。",	"0",	"0",	"0",	"0"},
[80] = {sortid = 80,	typeid = 2,	chapterid = 4,	chaptersort = 2,	number = "1-2",	name = "“保姆”",	plot_first = "YM/YM_1_2",	plot_last = "0",	checkpointid = 900202,	picture = "ArtGallery/PlotCg/YM/ym_2",	introduction = "身世充滿謎團的芙露露正式成為了月冕小隊的一員，新的月冕小隊準備進行第一次作戰。與此同時，莉莉絲與斯卡蒂拜託領航尋找芙露露的真正檔案。",	"0",	"0",	"0",	"0"},
[81] = {sortid = 81,	typeid = 2,	chapterid = 4,	chaptersort = 3,	number = "1-3",	name = "海岸上哭泣的少女",	plot_first = "YM/YM_1_3",	plot_last = "0",	checkpointid = 900203,	picture = "ArtGallery/PlotCg/YM/ym_3",	introduction = "作戰結束，月冕小隊將海面上大部分的水母殲滅，但是不知為何，除了摩根勒菲之外，所有人似乎都被那螢光色的海水迷住了，她們駐留在海灘上久久不忍離去……",	"0",	"0",	"0",	"0"},
[82] = {sortid = 82,	typeid = 2,	chapterid = 4,	chaptersort = 4,	number = "1-4",	name = "以小小溫暖對抗",	plot_first = "YM/YM_1_4",	plot_last = "0",	checkpointid = 900204,	picture = "ArtGallery/PlotCg/YM/ym_4",	introduction = "莉莉絲帶來了搜集來的芙露露的資料，還有她父母的資料，莉莉絲斷定芙露露通過幻象看到了什麼，並沉浸在其中，她想讓芙露露擺脫幻想。在她為“魔術”做準備的時候，從洛尼絲那裡得知芙露露擅自幻化參與了收尾工作。",	"0",	"0",	"0",	"0"},
[83] = {sortid = 83,	typeid = 2,	chapterid = 4,	chaptersort = 5,	number = "1-5",	name = "少女停止了流淚",	plot_first = "YM/YM_1_5",	plot_last = "0",	checkpointid = 900205,	picture = "ArtGallery/PlotCg/YM/ym_5",	introduction = "海灘上最後僅存的水母們開始了【進化】，原罪們瘋狂地集中在一起，把一小塊區域化作了怪誕的絞肉機，它們展開最後了的反擊。",	"0",	"0",	"0",	"0"},
[84] = {sortid = 84,	typeid = 2,	chapterid = 4,	chaptersort = 6,	number = "1-6",	name = "芙露露看見了",	plot_first = "YM/YM_1_6",	plot_last = "0",	checkpointid = 900206,	picture = "ArtGallery/PlotCg/YM/ym_6",	introduction = "月冕小隊作為緊急處理小隊參與了討伐戰，洛尼絲提議讓芙露露隨著大部隊撤離，但是月冕小隊遵從了芙露露的意志，讓她留在了戰場。",	"0",	"0",	"0",	"0"},
[85] = {sortid = 85,	typeid = 2,	chapterid = 4,	chaptersort = 7,	number = "1-7",	name = "月冕",	plot_first = "YM/YM_1_7",	plot_last = "0",	checkpointid = 900207,	picture = "ArtGallery/PlotCg/YM/ym_7",	introduction = "最初的芙露露難以融入月冕的戰鬥節奏，在摩根勒菲和莉莉絲的引導下，第一次主動進入【偽通感】狀態，與斯卡蒂一起擊破了巨大原罪的觸手。",	"0",	"0",	"0",	"0"},
[86] = {sortid = 86,	typeid = 2,	chapterid = 4,	chaptersort = 8,	number = "1-8",	name = "第四位魔法少女",	plot_first = "YM/YM_1_8",	plot_last = "0",	checkpointid = 900208,	picture = "ArtGallery/PlotCg/YM/ym_8",	introduction = "戰後，芙露露收穫了即使不用通感也能體會到的溫暖感情——寄給“月冕”的感謝信，並且暫時地找到了自己想要幹的事情，沒有目標的她想要繼承父母的目標，也是所有人的最終目標，驅逐原罪，奪回人類的家園。",	"0",	"0",	"0",	"0"},
